import Axios from 'axios';
import React, { useState } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Nav from './nav';

function App() {

const getJoke=() =>{

const [joke,setJoke]=useState("");
Axios.get("https://oceannews.herokuapp.com/news/allnews/").then(
  (response) =>{
    console.log(response);
    setJoke(response.data.id+"......"+response.data.username+
    response.data.author+"......"+response.data.title+
    response.data.description+"......"+response.data.imageurl+
    response.data.publishdate);
  }
);
};

  return (
    <div>
      < Nav />
     <h1 className="text-center">OceanNews</h1> 
     <button onClick={getJoke}>Get the Joke right now</button>
    {joke}
    </div>

  );
} 

export default App;
