import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Nav from './nav';
import Question from './Question';
import Grid from '@material-ui/core/Grid';
import Card from "react-bootstrap/Card";

import { Container, Row, Col } from 'reactstrap';


const Quiz = () =>{
  const [questions,setQuestions]=useState(null);
  const [count]=useState(0);
  useEffect(() => {
    const getApi = () =>{
      axios
        .get("https://oceannews.herokuapp.com/news/allnews/",
        )
        .then((res)=>setQuestions(res.data))
        .catch((err)=>console.log(err));
    };
    if(!questions){
      getApi();
    }
    console.log(questions);
  },[questions,count]);
  if(!questions) return <p className="text-light text-muted">Loading...</p>;

  return (
    <div>
      < Nav />


    <h1 className="text-center">OceanNews</h1> 
    {questions.map((question) =>
    <div>
 <Container>
         <Row>
        <Col xs="3">
        <Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src={question.imageurl} />
  <Card.Body>
    <Card.Title>{question.title}</Card.Title>
    <Card.Text>
    {question.description}
    </Card.Text>
    <p class="card-text"><small class="text-muted">Author : {question.author}</small></p>
    <p class="card-text"><small class="text-muted">Publish Date : {question.publishdate}</small></p>
  </Card.Body>
</Card>

        </Col>
      </Row>


        </Container>


</div>
    )}




    </div>


  );
} 

export default Quiz;
